///<summary>Represente igk_agent_ieversion function</summary>
function igk_agent_ieversion(){
    if(igk_agent_isie()){
        $tab=array();
        $c=preg_match_all("/(?<type>(MSIE|Trident|Edge))(\/)?\s*(?P<version>[0-9\.]+)/i", $_SERVER["HTTP_USER_AGENT"], $tab);
        if($c>=1){
            $t=igk_getv($tab["type"], 0);
            $v=igk_getv($tab["version"], 0);
            switch($t){
                case 'Trident':
                return "11.0/".$v;
                case 'Edge':
                return "12.0/".$v;
                case 'MSIE':default: 
                return $v;
            }
        }
    }
    return null;
}
