///<summary>Represente igk_android_onrenderdoc function</summary>
///<param name="args"></param>
function igk_android_onrenderdoc($args){
    $doc=$args->args[0];
    $options=$args->args[1];
    $v_isandroid=igk_agent_isandroid();
    if($options->Cache){
        $js=igk_createnode("Script");
        $js->
        Content="igk.android.init('width=device-width, initial-scale=1, user-scalable=no');";
        $m=new IGKHtmlSingleNodeViewer($js);
        $doc->body->add($m);
    }
    else{
        if($options->Context == IGK_WEB_CONTEXT){
            $sm=$doc->getMetas();
            if($sm){
                if($v_isandroid){
                    $meta=$sm->getMetaById("android:viewport");
                    if($meta == null){
                        $meta=igk_createnode("meta");
                        $meta["name"]="viewport";
                    }
                    $meta[IGKHtmlMetaManager::
                    ATTR_CONTENT]="width=device-width, initial-scale=1, user-scalable=no";
                    $sm->addMeta("android:viewport", $meta);
                }
                else{
                    $sm->rmMeta("android:viewport");
                }
            }
            else{
                igk_die("No meta defined for  [".$document."]");
            }
            if($v_isandroid){
                $doc->
                body["class"]="+igk-android";
            }
            else{
                $doc->
                body["class"]="-igk-android";
            }
        }
    }
}
