///<summary>check if apache module is present</summary>
function igk_apache_module($n){
    if(function_exists("apache_get_modules")){
        return in_array($n, apache_get_modules());
    }
    else{
        ob_start();
        phpinfo();
        $s=ob_get_contents();
        ob_end_clean();
        return strpos($s, $n) !== false;
    }
}
