///<summary>Represente igk_array_copy function</summary>
///<param name="c"></param>
///<param name="from"></param>
///<param name="to" default="-1"></param>
function igk_array_copy($c, $from=0, $to=-1){
    $tab=array();
    $t=($to == -1) ? count(c): $to;
    if($t<=igk_count($c)){
        foreach($c as $k=>$v){
            $tab[$k]=$v;
            $t--;
            if($t<=0)
                break;
        }
    }
    return $tab;
}
///<summary> create a key array for value</summary>
function igk_array_createkeyarray($tab, $default=1){
    $tab1=array();
    foreach($tab as $k=>$v){
        $tab1[$v]=$default;
    }
    return $tab1;
}
///<summary>Represente igk_array_fill function</summary>
///<param name="tab"></param>
///<param name="size"></param>
///<param name="default"></param>
function igk_array_fill($tab, $size=0, $default=0){
    if(($s=igk_count($tab)) < $size){
        while($s > 0){
            $tab[]=$default;
            $s--;
        }
    }
    return $tab;
}
