///<summary>compare two version</summary>
function igk_cmp_version($v1, $v2){
    while(($tb1=explode(".", trim($v1))) && count($tb1) < 4){
        $v1 .= ".0";
    }
    while(($tb2=explode(".", trim($v2))) && count($tb2) < 4){
        $v2 .= ".0";
    }
    $c=igk_count($tb1);
    if($c == igk_count($tb2)){
        $i=0;
        while(($i < $c) && ($tb1[$i] === $tb2[$i])){
            $i++;
        }
        if($i < $c){
            if($tb1[$i] < $tb2[$i]){
                return -1;
            }
            return 1;
        }
    }
    return strcmp($v1, $v2);
}
