function igk_conf_get($conf, $path, $default=null, $strict=0){
    $tab=null;
    $tobj=array();
    array_push($tobj, array('o'=>$conf, 'path'=>$path));
    $tout=null;
    $q=null;
    while($cq=array_pop($tobj)){
        $g=explode("/", $cq['path']);
        $q=$cq["o"];
        $count=0;
        foreach($g as $k){
            $count++;
            if(preg_match_all("/^\[(?P<exp>(.)+)\]$/i", trim($k), $tab) > 0){
                $o=igk_conf_get_expression($tab["exp"][0]);
                $m=null;
                if(is_array($q)){
                    foreach($q as $s){
                        $p=1;
                        foreach($o as $qt=>$qs){
                            $p=$p && igk_conf_match($s, $qt, $qs);
                            if(!$p)
                                break;
                        }
                        if($p){
                            if($m == null)
                                $m=$s;
                            else{
                                if(!is_array($m)){
                                    $m=array($m);
                                }
                                $m[]=$s;
                            }
                        }
                    }
                    if($m){
                        if($count < igk_count($g)){
                            $cpath=implode("/", array_slice($g, $count));
                            if(is_array($m)){
                                foreach($m as $mk=>$mv){
                                    array_push($tobj, array("o"=>$mv, "path"=>$cpath));
                                }
                            }
                            else{
                                array_push($tobj, array("o"=>$m, "path"=>$cpath));
                            }
                            $m=null;
                            $q=null;
                            break;
                        }
                        $q=$m;
                        continue;
                    }
                    else{
                        if($strict){
                            return $default;
                        }
                    }
                    $q=igk_getv($q, 0);
                    continue;
                }
                else{
                    $p=1;
                    foreach($o as $qt=>$qs){
                        $p=$p && igk_conf_match($q, $qt, $qs);
                        if(!$p)
                            break;
                    }
                    if($p)
                        $m=$q;
                    else{
                        if($strict)
                            return $default;
                        $m=$q;
                    }
                }
                if($m){
                    $q=$m;
                    continue;
                }
                return $default;
            }
            $q=igk_getv($q, $k);
            if($q == null)
                return $default;
        }
        if($q){
            if($tout === null){
                $tout=$q;
            }
            else if(!is_array($tout)){
                $tout=array($tout);
                $tout[]=$q;
            }
        }
    }
    return $tout;
}
