///<summary>get if configuration match the request</summary>
function igk_conf_match($t, $n, $op){
    if(isset($t->$n)){
        switch($op->op){
            case "=":
            return $t->$n == $op->v;
            break;
            case "$":
            return igk_str_endwith($t->$n, $op->v);
            break;
            case "^":
            return igk_str_startwith($t->$n, $op->v);
            case "~":
            return strstr($t->$n, $op->v) !== null;
        }
    }
    return 0;
}
