    $bindv=function($c, $v){
        $tab=array();
        array_push($tab, (object)array('n'=>$c, 'v'=>$v));
        while($q=array_pop($tab)){
            $m=$q;
            foreach($m->v as $s=>$t){
                if($s[0] == "@"){
                    $m->n[substr($s, 1)
                    ]=$t;
                    continue;
                }
                if(is_string($t) || is_numeric($t)){
                    $m->n->add($s)->
                    Content=htmlspecialchars($t);
