                        }
                        else{
                            array_push($tab, (object)array('n'=>$m->n->add($s), 'v'=>$t));
                        }
                    }
                }
            }
        }
    };
    if(is_string($v) || is_numeric($v)){
        $d->add($k)->
        Content=$v;
        return;
    }
    else{
        if(is_object($v)){
            $c=$d->add($k);
