    if($package !== null){
        $tab=is_array($args) ? $args: func_get_args();
        while(igk_count($tab) < 3){
            $tab[]=null;
        }
        list($name, $attributes, $indexOrArgs)
        =$tab;
        if(is_object($package) && method_exists($package, "CreateNode")){
            $n=$package->CreateNode($name, $attributes, $indexOrArgs);
            return $n;
