///<summary>Represente igk_css_balafon_index function</summary>
///<param name="dir"></param>
function igk_css_balafon_index($dir){
    header('Content-Type: text/css; charset=UTF-8');
    $c=igk_getr("Cache");
    if($c){
        igk_wl("/*Request from cache*/");
        return;
    }
    if(!defined("IGK_BASE_DIR")){
        define("IGK_BASE_DIR", $dir);
    }
    igk_initenv($dir);
    igk_start_session();
    ob_clean();
    IGKOb::Start();
    $doc=igk_app()->Doc;
    if($doc){
        igk_set_env("sys://css/cleartemp", __FUNCTION__);
        @session_write_close();
        $vsystheme=$doc->getSysTheme();
