        $n="";
        if(is_object($ctrl)){
            $n=$ctrl->Name;
        }
        else
            $n=$ctrl;
        $css_m=$n ? ".".strtolower(igk_css_str2class_name($n)): '';
        unset($n);
    }
    $def=$theme->def;
    $cl=& $theme->getCl();
    $referer=igk_getv($_SERVER, "HTTP_REFERER", "igk://system");
    include($f);
    $cl=& $theme->getCl();
    if(isset($root) && is_array($root)){
        $v_root=$def[":root"] ?? "";
        foreach($root as $k=>$v){
            if(empty($v))
                continue;
            $v_root .= $k.":".$v.";";
