///<summary>Represente igk_css_bind_theme_file function</summary>
///<param name="th"></param>
///<param name="files"></param>
function igk_css_bind_theme_file($th, $files){
    $f=igk_io_expand_path($files);
    $lfile=explode(";", $f);
    $v_lfiles=array();
    foreach($lfile as $d){
        $tab=explode('|', $d);
        list($file, $ctrl)
        =igk_count($tab)>=2 ? $tab: [$tab[0], null];
        if(!isset($v_lfiles[$file]) && !empty($d) && file_exists($file)){
            igk_css_bind_file(igk_getctrl($ctrl) ?? $ctrl, $file, $th);
            $v_lfiles[$file]=1;
        }
    }
}
///<summary>bind file from theme</summary>
function igk_css_bind_theme_files($theme){
    $files=$theme->def->getFiles();
    $lfile=explode(";", igk_io_expand_path($files));
    foreach($lfile as $d){
        if(empty($d))
            continue;
        if(strpos($d, '|') === false)
            $d .= "|";
        list($file, $ctrl)
        =explode('|', $d);
        if(file_exists($file)){
            igk_css_bind_file(null, $file, $theme);
        }
        else{
            igk_ilog("File not found - [{$d}]", __FUNCTION__);
        }
    }
}
