    $tab=array();
    if($tb){
        $is_callable=is_callable($key);
        $s="";
        foreach($tb as $k=>$v){
            $s=$key;
            if($is_callable){
                $s=$key($v);
                $tab[$s]=$v;
            }
            else{
                $tab[$v->
                $s]=$v;
            }
        }
    }
    return $tab;
}
