///<summary>Represente igk_db_view_result_node function</summary>
///<param name="result"></param>
///<param name="max" default="-1"></param>
function igk_db_view_result_node($result, $max=-1){
    if(!$result || !igk_reflection_class_implement($result, 'IIGKQueryResult')){
        return null;
    }
    $n=igk_createnode("NoTagNode");
    $r=$n->addTable();
    if($result->getResultType() == "boolean"){
        $tr=$r->addTr();
        $tr->add("th")->
        Content="result";
        $tr=$r->addTr();
        $tr->addTd()->
        Content=$result->getRowAtIndex(0);
        return $r;
    }
    $tr=$r->addTr();
    $key=array();
    foreach($result->Columns as $k=>$v){
        $tr->add("th")->
        Content=$v->name;
        $key []=$v->name;
    }
    if($max > 0){
        igk_html_paginate($r, $n, $result->Rows, $max, function($table, $k, $v) use ($key){
            $tr=$table->addTr();
            foreach($key as $j){
                $tr->addTd()->
                Content=$v->$j;
            }
        }
        , "", 1);
    }
    else{
        foreach($result->Rows as $k=>$v){
            $tr=$r->addTr();
            foreach($key as $j){
                $tr->addTd()->
                Content=$v->$j;
            }
        }
    }
    return $n;
}
