///<summary>Represente igk_debug_show function</summary>
///<param name="msg"></param>
function igk_debug_show($msg){
    if(!IGKApp::IsInit())
        return;
    if(IGKServerInfo::IsLocal()){
        $tab=explode(':', $msg);
        $ctrl=igk_getctrl("igkdebugctrl", false);
        if($ctrl == null)
            return;
        $lb=igk_createnode("div");
        if(count($tab) > 1){
            $args=array();
            preg_match_all('/((?P<name>([^:]+)):(?P<value>(.)*))$/i', $msg, $args);
            $n=strtolower(trim($args["name"][0]));
            switch($n){
                case "warning":
                case "error":
                case "notice":
                case "info":
                $lb["class"]="igk-debug-".$n;
                $lb->
                Content=$args["value"][0];
                $ctrl->addMessage($lb);
                break;default: 
                $lb["class"]="igk-debug-msg";
                $lb->
                Content=$msg;
                $ctrl->addMessage($lb);
                break;
            }
        }
        else{
            $lb["class"]="igk-debug-msg";
            $lb->
            Content=$msg;
            $ctrl->addMessage($lb);
        }
    }
}
