///<summary>utility to do foreach on table or object with a callback</summary>
function igk_foreach($i, $callback){
    if(!$i){
        return;}
    if(is_string($i)){
        return $callback(0, $i);
    }
    foreach($i as $k=>$v){
        $callback($k, $v);
    }
}
