///<summary>retrieve an array of session info</summary>
function igk_get_all_sessions(){
    $d=ini_get("session.save_path");
    $dt=null;
    $ssid=session_id();
    $v_capp=igk_app();
    session_write_close();
    $sess_key=IGK_APP_SESSION_KEY;
    $_SESSION[$sess_key]=null;
    if(is_dir($d)){
        $dt=array();
        $tab=igk_io_getfiles($d);
        sort($tab);
        foreach($tab as $k){
            if(preg_match("/^".IGK_SESSION_FILE_PREFIX."/i", basename($k))){
                $id=substr(basename($k), strlen(IGK_SESSION_FILE_PREFIX));
                $obj=(object)["location"=>$k, "sessid"=>$id, "start"=>"00:00:00", "ip"=>"127.0.0.1"];
                if($ssid == $id){
                    $app=$v_capp;
                }
                else{
                    unset($_SESSION[$sess_key]);
                    session_id($id);
                    session_start();
                    $app=$_SESSION[$sess_key];
                    session_write_close();
                    unset($_SESSION[$sess_key]);
                }
                if($app){
                    $obj->
                    start=$app->getcreateAt();
                    $serverInfo=$app->getServerInfo();
                    if($serverInfo){
                        $obj->
                        ip=igk_getv($serverInfo, "ip");
                        $obj->
                        serverInfo=$serverInfo;
                    }
                }
                $dt[]=$obj;
            }
        }
    }
    unset($_SESSION[$sess_key]);
    session_id($ssid);
    session_start();
    $_SESSION[$sess_key]=$v_capp;
    return $dt;
}
