///<summary>return the map selection fo the node</summary>
///<return >array of selection</return>
function igk_get_selector_map($node){
    $o=array();
    $toi=IGK_STR_EMPTY;
    $c=0;
    while($node){
        $oi="((\s|^)(".$node->tagName;
        $h=($cl=$node["class"]) ? $cl->getValue(): null;
        $id=$node["id"];
        $tf=IGK_STR_EMPTY;
        if(!empty($id)){
            $tf .= "(#".$id.")";
        }
        if(empty($h) == false){
            $b=explode(" ", $h);
            if(count($b) > 0){
                $i=0;
                $rk=IGK_STR_EMPTY;
                foreach($b as $r=>$s){
                    if(!empty($s)){
                        if($i)
                            $rk .= "|";
                        $rk .= "\.".$s;
                        $i=1;
                    }
                }
                if($i){
                    if(!empty($tf)){
                        $tf .= "|(".$rk.")";
                    }
                    else
                        $tf .= "(".$rk.")";
                }
            }
        }
        if(!empty($tf))
            $oi .= $tf."*|".$tf;
        $oi .= ")";
        if(empty($toi))
            $toi=$oi."$)".$toi;
        else{
            $toi="(((".$oi.")"."(|>))*".$toi."))";
        }
        $node=$node->getParentNode();
        if(!$node || ($node->tagName == "body")){
            break;
        }
        $c++;
        $o[]="/(".$toi.")/im";
    }
    $o[]="/(".$toi.")/im";
    return $o;
}
