///<summary>convert header string to associative array</summary>
function igk_header_str2array($s){
    $tab=array();
    $ot=array();
    $n="";
    $tab=explode("\r\n", $s);
    foreach($tab as $line){
        if(empty(trim($line)))
            continue;
        $ton=explode(":", $line);
        $on=trim($ton[0]);
        if(!empty($on) && preg_match("/^[a-z-]+$/i", trim($on))){
            $n=$on;
            $v=trim(substr($line, strpos($line, ":") + 1));
        }
        else{
            $v=$ot[$n]."\r\n".$line;
        }
        $ot[$n]=$v;
    }
    return $ot;
}
///<summary>Represente igk_hook function</summary>
///<param name="name"></param>
///<param name="args"></param>
function igk_hook($name, $args=array()){
    $hooks=igk_environment()->{"sys://hooks"};
    if(!$hooks){
        $hooks=array();
    }
    $tab=igk_getv($hooks, $name);
    if($tab){
        $list=& $tab->list;
        if($tab->changed){
            usort($list, function($a, $b){
                if($a->priority < $b->priority)
                    return -1;
                if($a->priority == $b->priority)
                    return 0;
                return 1;
            });
            $tab->
            changed=0;
        }
        $cargs=array((object)array("args"=>$args, "handle"=>0));
        foreach($list as $k=>$v){
            call_user_func_array($v->callback, $cargs);
            if($cargs[0]->handle){
                break;
            }
        }
    }
}
