///<summary>utility used to build a form</summary>
///<exemple>
///igk_html_build_form_array($node, ["clName"=>["type"=>]]
///</exemple>
function igk_html_build_form_array($ul, $param, $targettagname="li"){
    foreach($param as $k){
        $id=$k[0];
        $type=strtolower($k[1]);
        $required=igk_getv($k, 2);
        $args=igk_getv($k, 3);
        $li=$ul->add($targettagname);
        $a=null;
        $lb=$li->add("label", array("for"=>$id));
        $lb->
        Content=R::ngets("lb.".$id)->getValue();
        if($required){
            $lb->setClass("clrequired");
        }
        switch($type){
            case "textarea":
            $a=$li->addTextArea($id);
            $a->
            Content=$args;
            break;
            case "radio":
            case "checkbox":
            $a=$li->addInput($id, $type);
            if(igk_getr($id)){
                $a["checked"]="true";
            }
            break;
            case "hidden":
            case "text":
            case "password":default: 
            $a=$li->addInput($id, $type);
            $a["type"]=strtolower($type);
            $a["value"]=$args ? igk_getv($args, "value", igk_getr($id)): igk_getr($id);
            break;
        }
        $a["id"]=
        $a["name"]=$id;
        $args=igk_getv($k, 3);
        if($args != null){
            $a->AppendAttributes($args);
        }
    }
}
