        foreach($tab as $k=>$v){
            $opt=$target->add("option");
            $s=$ksu ? igk_getv($v, $kv): $v;
            $opt["value"]=$idxk ? $k: $s;
            if($resolvtext)
                $opt->
            Content=R::ngets("enum.".($ksu ? igk_getv($v, $dv): $v));
            else
                $opt->
            Content=$ksu ? igk_getv($v, $dv): $v;
            if($s == $selectedvalue){
