///<summary>Represente igk_html_index_of function</summary>
///<param name="node"></param>
function igk_html_index_of($node){
    $p=$node->getParentNode();
    if($p == null)
        return null;
    $i=0;
    foreach($p->Childs as $k=>$v){
        if($v === $node)
            return $i;
        $i++;
    }
    return $i;
}
