}
///<summary>Represente igk_html_load_menu_array function</summary>
///<param name="target"></param>
///<param name="tab"></param>
///<param name="item" default="li"></param>
///<param name="subnode" default="ul"></param>
///<param name="user" default="null"></param>
///<param name="ctrl" default="null"></param>
///<param name="callback" default="null"></param>
function igk_html_load_menu_array($target, $tab, $item="li", $subnode="ul", $user=null, $ctrl=null, $callback=null){
    $mi=null;
    $sfc=function($tab){
        $o=array();
        $c=0;
        $levels=array();
        $roots=0;
        foreach($tab as $k=>$v){
            $st=igk_io_basenamewithoutext($k);
            $v_isr=$st === $k;
            $c=0;
            if($v_isr){
                $c=$roots;
                $roots++;
            }
            else{
                if(isset($level[$st]))
                    $c=$level[$st];
            }
            $obj=(object)array(
                "key"=>$k,
                "index"=>null,
                "level"=>$v_isr ? 0: igk_count(explode(".",
                $st))
            );
            if(is_array($v)){
                $kk=igk_getv($v, "index");
                $obj->
                index=($kk ? $kk: $c++);
            }
            else{
                $obj->
                index=$c;
                $c++;
            }
            $o[]=$obj;
            if(!$v_isr)
                $level[$st]=$c;
        }
        return $o;
    };
    $h=$sfc($tab);
    usort($h, function($a, $b){
        if($a->level == $b->level){
            if($a->index == $b->index)
                return strcmp(strtolower($a->key), strtolower($b->key));
            return $a->index < $b->index ? -1: 1;
        }
        return $a->level < $b->level ? -1: 1;
    });
    $root=array();
    $sd=array();
    foreach($h as $obj){
        $s=$tab[$obj->key];
        $k=strtolower($obj->key);
        $pname=igk_io_basenamewithoutext($k);
        $mi=$target;
        $ii=null;
        if(!isset($root[$k])){
            if(isset($sd[$pname])){
                $ii=$sd[$pname];
                if($ii->ul == null){
                    $ii->
                    ul=$ii->li->add($subnode);
                }
                $mi=$ii->ul;
            }
            else{
                if((empty($pname) || !isset($root[$pname]))){
                    $root[$k]=(object)array("li"=>null, "ul"=>null, 'level'=>$obj->level);
                    $sd[$k]=$root[$k];
                }
                else{
                    $ii=$root[$pname];
                    $mi=$ii->li->add($subnode);
                }
            }
            if($mi !== $target){
                $mi["class"]="sub s".$obj->level;
                if($callback)
                    $callback($mi, "subi", $pname);
            }
        }
        else{
            igk_die("10: already define ");
        }
        if(is_array($s)){
            $u=igk_getv($s, "uri");
            if($ctrl){
                $u=$ctrl->getAppUri($u);
            }
            $auth=igk_getv($s, "auth", true);
            $ajx=igk_getv($s, "ajx");
            $lkey=igk_getv($s, "lkey", "menu.".$k);
            $init=igk_getv($s, "init");
            if((false === $auth) || (is_string($auth) && !igk_sys_isuser_authorize($user, $auth))){
                continue;
            }
            $hi=$mi->add($item);
            if($hi == null){
                igk_die($item." create null");
            }
            if($ajx){
                $hi->addAJXA($u)->
                Content=R::ngets($lkey);
            }
            else
                $hi->addA($u)->
            Content=R::ngets($lkey);
            if($init){
                $init($hi);
            }
        }
        else{
            if($mi == null){
                $mi=$target;
            }
            $hi=$mi->add($item);
            if($ctrl){
                $s=igk_str_rm_start($s, "/");
                $s=$ctrl->getAppUri($s);
            }
            $hi->addA($s)->
            Content=R::ngets("menu.".$k);
            $sd[$k]->
            li=$hi;
        }
    }
    $target->
    roots=$root;
}
