///<summary>Represente igk_html_query_parse function</summary>
///<param name="s"></param>
function igk_html_query_parse($s){
    $ln=strlen($s);
    $pos=0;
    $o="";
    while($ln > $pos){
        $ch=$s[$pos];
        switch($ch){
            case '`':
            $h=igk_str_read_brank($s, $pos, '`', '`');
            $o .= "<font color='#67009D'>".$h."</font>";
            break;
            case "'":
            $h=igk_str_read_brank($s, $pos, '\'', '\'');
            $o .= "<font color='#A90A07'>".$h."</font>";
            break;default: $o .= $ch;
            break;
        }
        $pos++;
    }
    return $o;
}
