///<summary>used to select in node expression</summary>
function igk_html_select($node, $expression){
    $r=array();
    $q=$node;
    $list=array();
    $searchmode=1;
    while($q){
        switch($searchmode){
            case 1:default: 
            if(strtolower($q->TagName) == $expression){
                $r[]=$q;
            }
            break;
        }
        if($q->HasChilds){
            foreach($q->Childs as $k){
                array_push($list, $k);
            }
        }
        $q=array_pop($list);
    }
    return $r;
}
