///<summary>Represente igk_html_strip_comment function</summary>
///<param name="c"></param>
function igk_html_strip_comment($c){
    while(($t=strpos($c, "<!--")) !== false){
        $end=strpos($c, "-->");
        if($end === false){
            $c=substr($c, 0, $t);
        }
        else{
            $c=substr($c, 0, $t).substr($c, $end + 3);
        }
    }
    return $c;
}
