///<summary>Represente igk_implode function</summary>
///<param name="glue"></param>
///<param name="tab"></param>
///<param name="callback"></param>
///<param name="ignore" default="1"></param>
function igk_implode($glue, $tab, $callback, $ignore=1){
    $o="";
    $g=0;
    foreach($tab as $k){
        $s=$callback($k);
        if(empty($s) && $ignore)
            continue;
        if($g)
            $o .= $glue;
        $o .= $s;
        $g=1;
    }
    return $o;
}
