    $file=$__tfile;
    unset($__tfile);
    if(isset($bindto)){
        $args=get_defined_vars();
        unset($args["bindto"]);
        $fc=function() use ($args, $target, $file){
            extract($args);
            $context="igk_include::callback";
            if($target)
                $t=$target;
            if(file_exists($file)){
                include($file);
            }
        };
        $fc=$fc->bindTo($bindto);
        $fc();
    }
    else{
        if($target)
            $t=$target;
        extract($args);
        $context="igk_include::inline";
        if(file_exists($file)){
            include($file);
        }
    }
