///<summary>Represente igk_io_createdir function</summary>
///<param name="dirname"></param>
///<param name="mode" default="0755"></param>
function igk_io_createdir($dirname, $mode=0755){
    if(preg_match("/^phar:/i", $dirname)){
        igk_wln_e("InvalidOperation#1200");
    }
    $pdir=array($dirname);
    $i=1;
    while($dirname=array_pop($pdir)){
        if(empty($dirname))
            return false;
        if(is_dir($dirname))
            continue;
        if(empty($dirname))
            return false;
        if(is_dir($dirname))
            continue;
        $p=dirname($dirname);
        if(empty($p))
            continue;
        if(is_dir($p)){
            @mkdir($dirname);
        }
        else{
            array_push($pdir, $dirname);
            array_push($pdir, dirname($dirname));
        }
        if($i > 10)
            break;
        $i++;
    }
    return igk_count($pdir) == 0;
}
