            $q=explode('/', igk_str_rm_start(explode('?', $rq)[0], '/'));
            $c=0;
            while(($m=array_shift($t)) && ($n=array_shift($q)) && ($m == $n)){
                if($c === 0)
                    $c="";
                $c .= "/".$m;
            }
            if($c !== 0){
                $rq=substr($rq, strlen($c));
            }
            return $rq;
        }
        else{
            return "/";
        }
    }
    return substr($doc_request, strlen($bdir));
}
