///<summary>Represente igk_io_fullpath function</summary>
///<param name="c"></param>
///<param name="base" default="null" ref="true"></param>
function igk_io_fullpath($c, & $base=null){
    $scheme="";
    $base=$base ?? getcwd();
    $d=igk_html_uri($c);
    if(preg_match("/^(\/|[a-z]+:\/\/)/i", $d, $data)){
        $scheme=$data[0];
        $d=substr($d, strlen($scheme));
    }
    $t=explode('/', $d);
    $b=[];
    foreach($t as $k){
        if(empty($k))
            continue;
        if($k == '..'){
            if(count($b) > 0){
                array_pop($b);
            }
            else{
                $base=dirname($base);
            }
        }
        else if($k == '.'){}
        else{
            $b[]=$k;
        }
    }
    return $scheme. implode('/', $b);
}
