///<summary>Represente igk_io_getrelativepath function</summary>
///<param name="source"></param>
///<param name="target"></param>
function igk_io_getrelativepath($source, $target){
    if(empty($target))
        return './';
    $o=IGK_STR_EMPTY;
    $v=$target;
    $sdir=$source;
    $i=strpos($target, $source);
    if($i !== false){
        $o="./". igk_str_rm_start(substr($v, $i + strlen($sdir)), '/');
    }
    else{
        $o=$v;
        $i=strpos($v, $sdir);
        $p="../";
        $sdir=dirname($sdir);
        $ci=0;
        while(!empty($sdir) && ($sdir != $v)){
            $ci++;
            if($sdir == ($gdir=dirname($sdir))){
                igk_trace();
                igk_wln_e("Window top dir dectected : ".$sdir, $source, $target, realpath($target));
            }
            $sdir=dirname($sdir);
            if(strpos($v, $sdir) === false){
                $p .= "../";
            }
            else
                break;
        }
        if($sdir){
            $o=igk_io_dir($p.igk_str_rm_start(substr($v, $i + strlen($sdir)), '/'));
        }
    }
    return $o;
}
