///<summary>Represente igk_io_locate_view_file function</summary>
///<param name="ctrl"></param>
///<param name="c" ref="true"></param>
///<param name="p" ref="true"></param>
function igk_io_locate_view_file($ctrl, & $c, & $p){
    $d=$ctrl->getViewDir()."/{$c}";
    $h=$p;
    if(is_dir($d)){
        if(is_string($h)){
            if(!preg_match("/\.".IGK_VIEW_FILE_EXT_REGEX."/i", $h)){
                if(!is_dir($c_d=$d."/".$h)){
                    $h .= ".phtml";
                }
            }
            if(file_exists($d."/".$h)){
                $c=$c."/".$p;
                $p=array();
            }
        }
        else if(is_array($h)){
            $cdir=$d;
            $ftype=0;
            $ctype=0;
            foreach($h as $d){
                $tfile=$cdir."/".$d;
                if(is_dir($tfile)){
                    $cdir .= $tfile;
                    $ftype=1;
                }
                else{
                    if(!preg_match("/\.".IGK_VIEW_FILE_EXT_REGEX."/i", $tfile)){
                        $tfile .= ".phtml";
                    }
                    if(file_exists($tfile)){
                        $cdir .= "/".$d;
                        $ftype=1;
                        $ctype++;
                    }
                    break;
                }
                $ctype++;
            }
            if($ctype > 0){
                $c .= "/".implode("/", array_slice($h, 0, $ctype));
                $p=array_slice($h, $ctype);
            }
        }
    }
}
