        $ext=igk_getv(["ejson"=>"json"], $ext, $ext);
        $rwf=igk_getr("rwf");
        $c1=$dir."/res.".$t.".".$ext;
        if(!file_exists($c1)){
            $c1=igk_io_basedir().dirname($query)."/res.".$t.".".$ext;
        }
        if(file_exists($c1)){
            igk_clear_header_list();
            igk_header_set_contenttype($ext);
            igk_header_cache_output($second);
            igk_zip_output(igk_io_read_allfile($c1));
