///<param name="file"></param>
function igk_io_store_uploaded_file($file){
    if(!IGKIO::CreateDir(dirname($file)))
        return 0;
    $fin=fopen("php://input", "r");
    if(!$fin)
        return 0;
    $fo=fopen($file, "w+");
    $buffsize=4096;
    while(($c=fread($fin, $buffsize))){
        fwrite($fo, $c, strlen($c));
    }
    fclose($fo);
    fclose($fin);
    return 1;
}
