///<summary>parse expression. multi json object expression</summary>
///<param name="exp">param or semi column expression</param>
function igk_json_array_parse($exp, & $err=null){
    $t=array();
    $st=array();
    $eof=0;
    $c=0;
    $ln=strlen($exp);
    $rm=0;
    $q=null;
    $v="";
    $_br=0;
    $k="";
    $err=array();
    $decimal_regex="/^(?P<_dec>(-)?([0-9]+(\\.|\\.([0-9]+)){0,1}))$/i";
    $identifier_regex="/^(?P<_identier>(null|((_|[a-z])([0-9a-z_])*)))$/i";
    while(!$eof && ($ln > $c)){
        $ch=$exp[$c];
        if($rm == 0x18){
            if($ch == ","){
                if($q){
                    $rm=0x2;
                }
            }
            $c++;
            continue;
        }
        switch($ch){
            case "{":
            if($rm == 0){
                $q=igk_createobj();
                $t[]=$q;
                $rm=0x2;
                $v="";
            }
            else if(($rm& 0x1) == 0x1){
                $v .= $ch;
            }
            else if($rm == 0x2){
                $err[]=("bad json .... 0x2");
                return null;
            }
            else{
                if($rm == 0x4){
                    $q->
                    $k=igk_createobj();
                    array_unshift($st, $q);
                    $q=$q->$k;
                    $rm=0x2;
                    $v="";
                }
                else{
                    $err[]="Not read litteral : read mode : ".$rm;
                    return null;
                }
            }
            break;
            case "}":
            if(($rm& 0x1) == 0x1){
                $v .= $ch;
            }
            else{
                if($rm == 0x10){
                    $q=array_shift($st);
                    if($q == null){
                        $rm=0;
                    }
                }
                else if($rm == 0x4){
                    if($v != null){
                        if((strlen($v) == 0) && empty($v)){
                            $err[]="Empty value detected. ";
                            return null;
                        }
                        $decimal=preg_match($decimal_regex, $v);
                        if($decimal && $v[strlen($v)-1] == ".")
                            $v .= "0";
                    }
                    $q->
                    $k=$v;
                    $v="";
                    $k="";
                    $rm=0x10;
                    $q=array_shift($st);
                    if($q == null){
                        $rm=0;
                    }
                }
                else{
                    if($rm != 0){
                        $err[]="possible not catchable object";
                        return $t;
                    }
                }
            }
            break;
            case "[":
            if(($rm == 0) || ($rm == 0x04)){
                $iv=igk_str_read_brank($exp, $c, ']', '[');
                $iv=trim($iv);
                if(!empty($iv)){
                    $ttab=igk_json_readarray($iv);
                    if($rm == 0){
                        $q=$ttab;
                        return $q;
                    }
                    else{
                        $q->
                        $k=$ttab;
                        $rm=0x1;
                    }
                }
                else{
                    $q->
                    $k=array();
                    $rm=0x10;
                    $c++;
                }
            }
            else
                $rm=0x08;
            break;default: 
            switch($rm){
                case 0x2:
                if(($ch == "\"") || ($ch == "'")){
                    $_br=$ch;
                    $rm=0x3;
                }
                else{
                    if($ch == ':'){
                        if(empty($v))
                            return null;
                        $k=$v;
                        $rm=0x4;
                        $v="";
                    }
                    else{
                        $v=trim($v.$ch);
                    }
                }
                break;
                case 0x3:
                if(($ch != $_br) || (($ch == $_br) && ($c > 0) && ($exp[$c-1] == "\\"))){
                    $v=$v.$ch;
                }
                else{
                    $k=$v;
                    $q->
                    $k=null;
                    $rm=0x21;
                    $v="";
                }
                break;
                case 0x4:
                if(($ch === "\"") || ($ch === "'")){
                    $_br=$ch;
                    $rm=0x05;
                }
                else if($ch == "["){
                    $rm=0x08;
                }
                else{
                    $t_s=trim($v.$ch);
                    if($t_s == '-'){
                        $dec=0;
                        $c++;
                        while(($c < $ln) && preg_match("/([0-9]|\.)/i", ($th=$exp[$c]))){
                            if($th == "."){
                                if($dec){
                                    $err[]="decimal value not valid";
                                    return null;
                                }
                                $dec=1;
                            }
                            $t_s .= $th;
                            $c++;
                        }
                        if($t_s == '-'){
                            $err[]="failed to read decimal negative decimal value";
                            return null;
                        }
                        unset($dec);
                        unset($th);
                    }
                    if(($ch != ',') && (empty($t_s) || preg_match($identifier_regex, $t_s) || preg_match($decimal_regex, $t_s))){
                        if($t_s == 'null'){
                            $v=null;
                        }
                        else
                            $v=$t_s;
                        if(!empty($t_s)){
                            $q->
                            $k=$v;
                            $v="";
                            $k="";
                            $rm=0x2;
                            $c++;
                        }
                    }
                    else{
                        $rm=0x2;
                        if($v != null){
                            $decimal=preg_match($decimal_regex, $v);
                            if($decimal && $v[strlen($v)-1] == ".")
                                $v .= "0";
                        }
                        if(is_numeric($v)){
                            $v=floatval($v);
                        }
                        $q->
                        $k=$v;
                        $v="";
                        $k="";
                    }
                }
                break;
                case 0x5:
                if(($ch != $_br) || ($ch == $_br && ($c > 0) && ($exp[$c-1] == "\\"))){
                    $v=$v.$ch;
                }
                else{
                    $q->
                    $k=$v;
                    $rm=0x10;
                    $v="";
                    $k=null;
                }
                break;
                case 0x8:
                $iv="";
                $iv=igk_str_read_brank($exp, $c);
                $iv=trim($iv);
                if(!empty($iv)){
                    $ttab=igk_json_readarray($iv);
                    $q->
                    $k=$ttab;
                }
                $rm=0x18;
                $v="";
                break;
                case 0x10:
                if($ch == ","){
                    $rm=0x2;
                }
                break;
                case 0x21:
                if($ch == ':')
                    $rm=0x4;
                break;
            }
            break;
        }
        $c++;
        if($eof)
            break;
    }
    return $t;
}
