///<summary>Represente igk_json_readarray function</summary>
///<param name="v"></param>
function igk_json_readarray($v){
    $rm=0;
    $ch=0;
    $pos=0;
    $ln=strlen($v);
    $iv="";
    $start=0;
    $tvalue=array();
    $q=null;
    $tdephArray=[];
    while($pos < $ln){
        $ch=$v[$pos];
        switch($ch){
            case "[":
            if($q == null){
                $q=& $tvalue;
            }
            else{
                $c=array();
                $q[]=& $c;
                array_push($tdephArray, $q);
                $q=& $c;
            }
            break;
            case ",":
            if($rm == 0x11){
                $rm=0;
            }
            else{
                if($rm == 0){
                    if(!empty($iv)){
                        $q[]=$iv;
                    }
                    else{
                        $q[]=null;
                    }
                }
                $iv="";
            }
            break;
            case "\"":
            case "'":
            if($rm == 0){
                $rm=1;
                $start=$ch;
                $iv="";
            }
            else if($rm == 1){
                if(($ch !== $start) || $v[$pos-1] == "\\"){
                    $iv .= $ch;
                }
                else{
                    $q[]=$iv;
                    $iv="";
                    $rm=0x11;
                }
            }
            break;
            case "{":
            if($rm == 0){
                $c=igk_str_read_brank($v, $pos, "}", "{");
                $obj=igk_json_parse($c);
                if($obj){
                    $q[]=$obj;
                }
                $iv="";
                $rm=0x11;
            }
            else
                $iv .= $ch;
            break;
            case "]":
            $_cd=trim($iv);
            if(!empty($_cd)){
                $q[]=$_cd;
            }
            $iv=null;
            if(count($tdephArray) > 0){
                $tvalue=array_pop($tdephArray);
                $q=& $tvalue;
                $rm=0x11;
            }
            break;default: $iv .= $ch;
            break;
        }
        $pos++;
    }
    return $tvalue;
}
