///<summary>Represente igk_log_var_dump function</summary>
///<param name="tab"></param>
function igk_log_var_dump($tab){
    if(is_numeric($tab) || is_bool($tab)){
        igk_wl($tab);
        igk_wl(IGK_LF);
        return;
    }
    $cl=array("array"=>"#84a");
    $s="";
    $LF=igk_is_cmd() ? IGK_LF: "<br />".IGK_LF;
    $is_obj=is_object($tab);
    $is_cmd=igk_is_cmd();
    if($is_obj){
        $s .= 'Type: '.get_class($tab);
    }
    else if(is_array($tab)){
        $s .= 'Type: ';
        if(!igk_is_cmd()){
            $s .= '<span style="color: '.$cl['array'].'">';
        }
        $s .= "IsArray: ";
        if(!$is_cmd)
            $s .= "</span>";
    }
    igk_wl($s);
    $msg=$LF."(".$LF;
    if($tab){
        foreach($tab as $k=>$v){
            $msg .= "\t{$k}";
            if(is_object($v)){
                $msg .= ":Object";
            }
            else if(is_array($v)){
                $msg .= ":Array";
            }
            else
                $msg .= " => ".$v;
            $msg .= $LF;
        }
    }
    igk_wl($msg.")");
}
