function igk_str_get_version($v){
    $tb=explode(".", $v);
    if(igk_count($tb) < 4){
        $c=igk_count($tb)-4;
        while($c > 0){
            $tb[]=0;
            $c--;
        }
    }
    else{
        $tb=array_shift($tb, 4);
    }
    return igk_str_join_tab($tb, '.', false);
