///<summary>glue data.</summary>
function igk_str_glue($mix=","){
    $tab=array_slice(func_get_args(), 1);
    if(igk_count($tab)<=0)
        return null;
    $s="";
    $fc=0;
    if(is_string($mix))
        $fc=function($v, & $s=null){
        return $v;
    };
    else if(is_callable($mix)){
        $fc=$mix;
        $mix="";
    }
    else
        return null;
    foreach($tab as $k=>$v){
        if(empty($v))
            continue;
        $h=$fc($v, $s);
        if(empty($s)){
            $s=$h;
        }
        else
            $s .= $mix.$h;
    }
    return $s;
}
