function igk_str_read_args($s){
    $count=strlen($s);
    $i=0;
    $tab=array();
    $v="";
    while($i < $count){
        $ch=$s[$i];
        $i++;
        switch($ch){
            case " ":
            if(!empty($v)){
                $tab[]=$v;
                $v=null;
            }
            break;
            case '"':
            if(empty($v)){
                $i--;
                $rgx=igk_str_read_brank($s, $i, $ch, $ch, null, 1);
                $v .= $rgx;
                $m=4;
                $i++;
                $tab[]=$v;
                $v=null;
            }
            else{
                $v .= $ch;
            }
            break;default: $v .= $ch;
            break;
        }
    }
    if(!empty($v)){
        $tab[]=$v;
    }
    return $tab;
}
