///<summary>split string data </summary>
function igk_str_split_string($splitchr, $str){
    $ln=strlen($str);
    $pos=0;
    $tab=array();
    $v="";
    $treat=function($v){
        $cv=trim($v);
        if(!empty($cv) && (($ln=strlen($cv)) > 1) && ($cv[0] == $cv[$ln-1]) && (strpos('"\'', $cv[0]) !== false)){
            return substr($cv, 1, $ln-2);
        }
        return $cv;
    };
    while($pos < $ln){
        $ch=$str[$pos];
        switch($ch){
            case "\"":
            case "'":
            $v .= igk_str_read_brank($str, $pos, $ch, $ch);
            $pos++;
            break;
            case $splitchr:
            $tab[]=$treat($v);
            $v="";
            break;default: $v .= $ch;
            break;
        }
        $pos++;
    }
    if(!empty($v)){
        $tab[]=$treat($v);
    }
    return $tab;
}
