        }
        $p=igk_sys_ac_create_pattern(null, null, $sk);
        if($p->matche($uri)){
            $request=explode("?", $u)[0];
            if(!isset($b[$request])){
                $b[$request]=$u;
                igk_set_env($uri_key, $b);
            }
