///<summary>Represente igk_time_max_info function</summary>
///<param name="d"></param>
///<param name="from" default="null"></param>
function igk_time_max_info($d, $from=null){
    $c=$from ?? time();
    $d=$d - $c;
    if($d<=0)
        return array($d, 'n');
    if($d < 60){
        return array($d, 's');
    }
    $d=round($d/60);
    if($d < 60)
        return array($d, 'm');
    $d=round($d/60);
    if($d<=24)
        return array($d, 'h');
    $d=round($d/24);
    if($d < 365)
        return array($d, 'd');
    $d=round($d / 365);
    return array($d, "y");
}
