            }
            return $tab;
            break;
            case 1:
            if($v_q->RowCount > 0){
                $v=$v_q->getRowAtIndex(0);
                return igk_user_build_info($r, $inf, $v, $ctrl);
            }
            return null;
            break;default: 
            if($v_n < 0){
                igk_die("error on data cardinality ".$n);
            }
            $v_tk=min($v_n, $v_q->RowCount);
            $tab=array();
            foreach($v_q->Rows as $k=>$v){
                $tab[]=igk_user_build_info($r, $inf, $v, $ctrl);
