///<summary>xml reader info</summary>
function igk_xml_read($xreader){
    $eof=0;
    $v_c=0;
    $v_enter=0;
    $q=$xreader;
    $v="";
    $canread=null;
    $init="::init";
    $xreader->
    attribs=null;
    if(!isset($xreader->$init)){
        if($xreader->context == "stream"){
            fseek($xreader->text, $xreader->offset);
            $hfile=$xreader->text;
            $xreader->
            offset=0;
            $canread=function() use (& $xreader, & $hfile, & $eof){
                if($eof)
                    return 0;
                $buffsize=$xreader->inf->bufferSize;
                if($xreader->length == -1){
                    $b=@fread($hfile, $buffsize);
                    if($b){
                        $xreader->
                        length=strlen($b);
                        $xreader->
                        text=$b;
                        return 1;
                    }
                    return 0;
                }
                else{
                    if($xreader->offset < $xreader->length)
                        return 1;
                    $b=fread($hfile, $buffsize);
                    $xreader->length += strlen($b);
                    $xreader->text .= $b;
                    return ($xreader->offset < $xreader->length);
                }
            };
        }
        else{
            if($xreader->offset>=$xreader->length)
                return 0;
            $canread=function() use (& $xreader, & $eof){
                return !$eof && ($xreader->offset < $xreader->length);
            };
        }
        $readnext=function($count) use ($q, & $canread){
            $readsub="";
            $ics=$q->offset;
            $q->offset++;
            $icx=$q->offset;
            $icc=$count;
            $ico=$icc;
            while($canread() && ($icc > 0) && ($m=$q->text[$icx])){
                $icc--;
                $readsub .= $m;
                $icx++;
                $q->offset++;
            }
            $q->
            offset=$ics;
            return $readsub;
        };
        $readname=function() use (& $xreader, $canread){
            $v=IGK_STR_EMPTY;
            $s="/".IGK_TAGNAME_CHAR_REGEX."/i";
            while($canread() && preg_match($s, $k=$xreader->text[$xreader->offset])){
                $v .= $k;
                $xreader->offset++;
            }
            return $v;
        };
        $__readTextValue=function() use (& $xreader, $canread){
            $q=$xreader;
            $v=IGK_STR_EMPTY;
            $p=$q->offset;
            while($canread()){
                $v .= $q->text[$q->offset];
                $q->offset++;
                if(substr($v, -1, 1) == "<"){
                    $v=substr($v, 0, strlen($v)-1);
                    if(empty(trim($v))){
                        $q->offset -= 2;
                        $q->
                        name=null;
                        $q->
                        value=null;
                        return false;
                    }
                    $q->offset--;
                    $q->
                    name=null;
                    $q->
                    value=$v;
                    $q->
                    nodetype=IGKXMLNodeType::TEXT;
                    return true;
                }
            }
            $q->
            offset=$p;
            return false;
        };
        $xreader->{"can:read"}
        =$canread;
        $xreader->{"can:readt"}
        =$__readTextValue;
        $xreader->{"can:readn"}
        =$readname;
        $xreader->{"can:readnx"}
        =$readnext;
        $xreader->
        $init=1;
    }
    else{
        $canread=$xreader->{"can:read"};
        $__readTextValue=$xreader->{"can:readt"};
        $readname=$xreader->{"can:readn"};
        $readnext=$xreader->{"can:readnx"};
    }
    $i=$xreader->offset;
    $xreader->
    context="xml";
    $r=0;
    while($r=$canread()){
        $c=$q->text[$q->offset];
        switch($c){
            case "<":
            $v_enter=true;
            break;
            case "?":
            if($v_enter){
                $q->offset++;
                $v=IGK_STR_EMPTY;
                while($canread()){
                    $v .= $q->text[$q->offset];
                    $q->offset++;
                    if(substr($v, -2, 2) == "?>"){
                        $v=substr($v, 0, strlen($v)-3);
                        $q->
                        name=null;
                        $q->
                        value=$v;
                        $q->
                        nodetype=\IGKXMLNodeType::PROCESSOR;
                        return true;
                    }
                }
            }
            else{
                return $__readTextValue();
            }
            return false;
            break;
            case "!":
            if($v_enter){
                $lf=substr($q->text, $q->offset + 1, 2);
                $readsub=$readnext(2);
                if($readsub == "--"){
                    $q->offset += 3;
                    $v=IGK_STR_EMPTY;
                    while($canread()){
                        $v .= $q->text[$q->offset];
                        $q->offset++;
                        if(substr($v, -3, 3) == "-->"){
                            $v=substr($v, 0, strlen($v)-3);
                            $q->
                            name=null;
                            $q->
                            value=$v;
                            $q->
                            nodetype=\IGKXMLNodeType::COMMENT;
                            return true;
                        }
                    }
                }
                else{
                    $readsub=$readnext(7);
                    if(strlen($readsub) == 7){
                        $readsub=strtoupper($readsub);
                        switch($readsub){
                            case "[CDATA[":
                            $q->offset += 8;
                            $v=IGK_STR_EMPTY;
                            while($canread()){
                                $v .= $q->text[$q->offset];
                                $q->offset++;
                                if(substr($v, -3, 3) == "]]>"){
                                    $v=substr($v, 0, strlen($v)-3);
                                    $q->
                                    name=null;
                                    $q->
                                    value=$v;
                                    $q->
                                    nodetype=\IGKXMLNodeType::CDATA;
                                    return true;
                                }
                            }
                            break;
                            case "DOCTYPE":
                            $q->offset += 8;
                            $v=IGK_STR_EMPTY;
                            while($canread()){
                                $v .= $q->text[$q->offset];
                                $q->offset++;
                                if(substr($v, -1, 1) == ">"){
                                    $v=substr($v, 0, strlen($v)-1);
                                    $q->
                                    name=null;
                                    $q->
                                    value=$v;
                                    $q->
                                    nodetype=\IGKXMLNodeType::DOCTYPE;
                                    return true;
                                }
                            }
                            break;
                        }
                    }
                    return false;
                }
                return false;
            }
            break;
            case "/":
            if($v_enter){
                $q->offset += 1;
                $q->
                nodetype=IGKXMLNodeType::ENDELEMENT;
                $q->
                name=$readname($q);
                $q->
                value=null;
                $v_enter=false;
                while(($v_c > $q->offset) && ($q->text[$q->offset] !== '>')){
                    $q->offset++;
                }
                return true;
            }
            $v .= $c;
            break;default: 
            if(!$v_enter){
                if($q->nodetype == \IGKXMLNodeType::ELEMENT){
                    $match=array();
                    switch(strtolower($q->name)){
                        case "script":
                        case "code":{
                            $tag=strtolower($q->name);
                            while($canread()){
                                $v .= $q->text[$q->offset];
                                $q->offset++;
                                if(preg_match("/\<\/([\s]*)".$tag."([\s]*)\>$/i", $v, $match)){
                                    $q->offset -= strlen($match[0]);
                                    $v=substr($v, 0, strlen($v) - strlen($match[0]));
                                    break;
                                }
                            }
                            $q->
                            name=null;
                            $q->
                            value=$v;
                            $q->
                            nodetype=\IGKXMLNodeType::TEXT;
                            return true;
                        }
                        break;
                        case "style":{
                            while($canread()){
                                $v .= $q->text[$q->offset];
                                $q->offset++;
                                if(preg_match("/(\<\/([\s]*)style([\s]*)>)$/i", $v, $match)){
                                    $q->offset -= strlen($match[0]);
                                    $v=substr($v, 0, strlen($v) - strlen($match[0]));
                                    break;
                                }
                            }
                            $q->
                            name=null;
                            $q->
                            value=$v;
                            $q->
                            nodetype=\IGKXMLNodeType::TEXT;
                            return true;
                        }
                        break;default: {
                            if($__readTextValue()){
                                return true;
                            }
                        }
                        break;
                    }
                }
                else{
                    $v=IGK_STR_EMPTY;
                    if($q->nodetype == IGKXMLNodeType::ENDELEMENT)
                        $q->offset++;
                    while($canread()){
                        $v .= $q->text[$q->offset];
                        $q->offset++;
                        if(substr($v, -1, 1) == "<"){
                            $v=substr($v, 0, strlen($v)-1);
                            if(empty(trim($v))){
                                $q->offset -= 2;
                                break;
                            }
                            $q->offset--;
                            $q->
                            name=null;
                            $q->
                            value=$v;
                            $q->
                            nodetype=\IGKXMLNodeType::TEXT;
                            return true;
                        }
                    }
                }
            }
            else{
                $q->
                name=$readname();
                $q->
                value=null;
                $q->
                nodetype=\IGKXMLNodeType::ELEMENT;
                $q->
                isEmpty=false;
                $q->
                hasAttrib=false;
                $v_end=false;
                $v=IGK_STR_EMPTY;
                $v_readname=false;
                $v_readvalue=false;
                $v_attribname=null;
                $v_ch=null;
                $v_startattribvalue=false;
                $v_attribmatch=IGK_STR_EMPTY;
                $v_bracketstart=false;
                $v_bracketch="";
                $v_expressions=array();
                while($canread()){
                    $v_ch=$q->text[$q->offset];
                    $q->offset++;
                    if(preg_match("/(\[)/", $v_ch)){
                        if(!$v_startattribvalue){
                            $exp=$v_ch. igk_str_read_brank($q->text, $q->offset, ']', '[');
                            $cout=igk_count($v_expressions);
                            $v_expressions[]=$exp;
                            $v .= "@igk:expression$cout=\"$cout\"";
                            continue;
                        }
                    }
                    $v .= $v_ch;
                    if(preg_match("('|\")", $v_ch)){
                        if($v_startattribvalue){
                            if($v_attribmatch == $v_ch)
                                $v_startattribvalue=false;
                        }
                        else{
                            $v_startattribvalue=true;
                            $v_attribmatch=$v_ch;
                        }
                    }
                    if($v_startattribvalue)
                        continue;
                    if(substr($v, -2, 2) == "/>"){
                        $v=trim(substr($v, 0, strlen($v)-2));
                        $q->
                        isEmpty=true;
                        break;
                    }
                    else if(substr($v, -1, 1) == ">"){
                        $v=substr($v, 0, strlen($v)-1);
                        $q->
                        isEmpty=false;
                        break;
                    }
                    if($v_readname == false){
                        if(preg_match("/([\s])/", $v_ch)){
                            $v_attribname=$v_ch;
                            $v_readname=true;
                            $v_readvalue=false;
                        }
                    }
                    else{
                        if(preg_match("/[\s]/", $v_ch))
                            $v_attribname .= $v_ch;
                        else{
                            $v_readname=false;
                        }
                    }
                }
                if(!empty($v)){
                    $q->
                    hasAttrib=true;
                    $m=null;
                    $machv="(?P<value>";
                    $machv .= "([\"](([^\"]*(')?(\"\")?)+)[\"])";
                    $machv .= "|([\'](([^']*(\")?('')?)+)[\'])";
                    $machv .= "|(([^\s]*)+)";
                    $machv .= ")";
                    $acount=preg_match_all("/(?P<name>(@?".IGK_TAGNAME_CHAR_REGEX."+))[\s]*=[\s]*(".$machv.")/i", $v, $m);
                    $q->
                    attribs=array();
                    for($cc=0; $cc < $acount; $cc++){
                        $k=$m["name"][$cc];
                        if(preg_match("/^@igk:expression/", $k)){
                            $q->
                            attribs[$k]=$v_expressions[IGKHtmlUtils::GetAttributeValue($m["value"][$cc], $q->context)];
                        }
                        else
                            $q->
                        attribs[$k]=IGKHtmlUtils::GetAttributeValue($m["value"][$cc], $q->context);
                    }
                }
                return true;
            }
            break;
        }
        ++$q->offset;
    }
    return $r;
}
///<summary>Represente igk_xml_read_attribute function</summary>
///<param name="s"></param>
///<param name="pos" ref="true"></param>
///<param name="outstring" default="null" ref="true"></param>
///<param name="securename" default="1"></param>
function igk_xml_read_attribute($s, & $pos=0, & $outstring=null, $securename=1){
    $ln=strlen($s);
    $m=0;
    $n="";
    $v="";
    $tab=array();
    while($pos < $ln){
        $ch=$s[$pos];
        switch($ch){
            case "=":
            if($m == 0){
                if(empty($n=trim($n))){
                    throw new IGKInvalidXmlReadException('Not a valid attribute', $pos);
                }
                $m=1;
                $v=$n;
                $n="";
                $outstring .= " ".$v."=";
                if($securename && !igk_xml_is_validname($v)){
                    throw new IGKInvalidXmlReadException("[".$v.'] is not a valid xml\'s attribute name', $pos);
                }
            }
            break;
            case "'":
            case "\"":
            if($m != 1 || !empty($n))
                throw new IGKInvalidXmlReadException('Not a valid attribute value', $pos);
            $n=igk_str_read_brank($s, $pos, $ch, $ch);
            $outstring .= $n;
            $n=substr($n, 1, strlen($n)-2);
            $tab[$v]=$n;
            $v=
            $n="";
            $m=0;
            break;
            case " ":
            if($m == 1){
                if(!empty($n=trim($n))){
                    $tab[$v]=$n;
                    $outstring .= $n;
                    $v=
                    $n="";
                    $m=0;
                    continue 2;
                }
            }
            $n .= $ch;
            break;
            case '>':
            if($m == 0)
                break 2;
            break;default: $n .= $ch;
            break;
        }
        $pos++;
    }
    if($m == 1){
        $tab[$v]=$n;
        $outstring .= $n;
    }
    return $tab;
}
///<summary>function to read xml content</summary>
///<param name='content'>xml string value</param>
///<param name='callback'>callback that will be call when read data</param>
///<param name='callback'>information used</param>
///<code type="php">
///$inf->path = path to rootnode
///$inf->objects list of all object on that root node populate
///</code>
function igk_xml_read_content($content, $callback, & $inf=null){
    $inf=igk_xml_create_readinfo($inf);
    $xreader=(object)[];
    $xreader->
    offset=$inf->offset;
    $xreader->
    text=$content;
    $xreader->
    length=strlen($content);
    $xreader->
    name="";
    $xreader->
    value="";
    $xreader->
    type="";
    $xreader->
    parent=null;
    $xreader->
    context="xml";
    $xreader->
    inf=$inf;
    $xreader->
    attribs=null;
    $xreader->
    nodetype=0;
    while(igk_xml_read($xreader)){
        if(!$callback($xreader, $inf)){
            igk_wln("bread for what");
            break;
        }
    }
    $inf->
    offset=$xreader->offset;
    igk_xml_unset_read_info($inf);
}
///<summary>Represente igk_xml_read_doctype function</summary>
///<param name="s"></param>
///<param name="pos" ref="true"></param>
function igk_xml_read_doctype($s, & $pos){
    $out="";
    $brank=array();
    $depth=0;
    $ln=strlen($s);
    while($pos < $ln){
        $ch=$s[$pos];
        switch($ch){
            case "[":
            case "(":
            $depth++;
            break;
            case "]":
            case ")":
            $depth--;
            break;
            case ">":
            if($depth<=0)
                break 2;
            break;
        }
        $out .= $ch;
        $pos++;
    }
    return $out;
}
///<summary>Represente igk_xml_read_node function</summary>
///<param name="xreader"></param>
function igk_xml_read_node($xreader){
    $n=$xreader->name;
    $n=igk_createxmlnode($n);
    $n->setAttributes($xreader->attribs);
    $g=$xreader->offset;
    igk_xml_read_skip($xreader);
    $d=$xreader->offset;
    $txt=substr($xreader->text, $g, $d - $g + 1);
    $n->load($txt);
    return $n;
}
///<summary>skiping reader</summary>
function igk_xml_read_skip($xreader){
    $q=$xreader;
    if($q->nodetype == IGKXMLNodeType::ELEMENT){
        if(!$q->isEmpty){
            $n=strtolower($q->name);
            $depth=0;
            $end=false;
            while(!$end && igk_xml_read($q)){
                switch($q->nodetype){
                    case IGKXMLNodeType::ELEMENT:
                    if(!$q->isEmpty){
                        $depth++;
                    }
                    break;
                    case IGKXMLNodeType::ENDELEMENT:
                    if(($depth == 0) && (strtolower($q->name) == $n)){
                        $end=true;
                    }
                    else if($depth > 0)
                        $depth--;
                    break;
                }
            }
            return $end;
        }
    }
    return false;
}
///<summary>read xml object from file memory stream</summary>
function igk_xml_read_stream($f, $callback, $inf){
    if(!file_exists($f))
        return;
    if($hf=fopen($f, "r")){
        $inf=igk_xml_create_readinfo($inf);
        $xreader=(object)[];
        $xreader->
        offset=$inf->offset;
        $xreader->
        text=$hf;
        $xreader->
        length=-1;
        $xreader->
        name="";
        $xreader->
        value="";
        $xreader->
        type="";
        $xreader->
        parent=null;
        $xreader->
        context="stream";
        $xreader->
        nodetype=0;
        $xreader->
        inf=$inf;
        $xreader->
        attribs=null;
        while(igk_xml_read($xreader)){
            if(!$callback($xreader, $inf)){
                break;
            }
        }
        $inf->
        offset=$xreader->offset;
        if($inf->count === null){
            $inf->
            count=igk_count($inf->objects);
        }
        fclose($hf);
        igk_xml_unset_read_info($inf);
    }
}
///<summary>used to read tagname</summary>
function igk_xml_read_tagname($content, & $pos){
    $n="";
    $ln=strlen($content);
    while(($pos < $ln) && ($ch=$content[$pos]) && ($ch != ">") && ($ch != " ")){
        $n .= $ch;
        $pos++;
    }
    return $n;
}
///<summary>used to read xml</summary>
///<param name="path">xpath object</param>
///<param name="callback">the callback(index) used to read valid data at index</param>
function igk_xml_read_xml($c, $path=null, & $inf=null, $callback=null){
    $inf=new StdClass();
    $inf->
    ln=strlen($c);
    $inf->
    content=$c;
    $inf->
    offset=0;
    $inf->
    out="";
    $path=igk_str_rm_start($path, "/");
    $inf->
    path=$path;
    $g=$path != null ? explode("/", $path): null;
    $o="";
    $t="0";
    $m=0;
    $depth=0;
    $skip_depth=0;
    $item=0;
    $start=0;
    $pathlevel=count($g);
    while($inf->offset < $inf->ln){
        $ch=$inf->content[$inf->offset];
        switch($ch){
            case "<":
            $m=1;
            break;
            case "?":
            if($m == 1){
                $gp=strpos($inf->content, "?>", $inf->offset + 1);
                if($gp == false){
                    throw new IGKInvalidXmlReadException("?&gt; close not found");
                }
                $s=substr($inf->content, $inf->offset + 1, $gp - $inf->offset-1);
                if($skip_depth == 0)
                    $o .= "<?".$s."?>";
                $inf->offset += ($gp);
                $m=0;
            }
            break;
            case "/":
            if($m == 1){
                if($depth == 0){
                    throw new IGKInvalidXmlReadException("Invalid XmlReadException - No root found");
                }
                $inf->offset++;
                $n=igk_xml_read_tagname($inf->content, $inf->offset);
                if($skip_depth > 0){
                    $skip_depth--;
                }
                else{
                    $o .= "</".$n.">";
                }
                $depth--;
                $m=0;
            }
            break;
            case "!":
            if($m == 1){
                if(substr($inf->content, $inf->offset + 1, 2) == "--"){
                    $inf->offset += 3;
                    $gp=strpos($inf->content, "-->", $inf->offset);
                    if($gp == false){
                        $s=substr($inf->content, $inf->offset + 1);
                        $inf->
                        offset=$inf->ln;
                    }
                    else{
                        $s=substr($inf->content, $inf->offset, $gp - $inf->offset);
                        $inf->
                        offset=$gp + 2;
                    }
                    if($skip_depth == 0)
                        $o .= "<!--".$s."-->";
                    $m=0;
                }
                else if(substr($inf->content, $inf->offset + 1, 7) == "[CDATA["){
                    $inf->offset += 8;
                    $gp=strpos($inf->content, "]]>", $inf->offset);
                    if($gp == false){
                        $s=substr($inf->content, $inf->offset);
                        $inf->
                        offset=$inf->ln;
                    }
                    else{
                        $s=substr($inf->content, $inf->offset, $gp - $inf->offset);
                        $inf->
                        offset=$gp + 2;
                    }
                    if($skip_depth == 0)
                        $o .= "<![CDATA[".$s."]]>";
                    $m=0;
                }
                else if(substr($inf->content, $inf->offset + 1, 8) == "DOCTYPE "){
                    $inf->offset += 8;
                    igk_xml_read_doctype($inf->content, $inf->offset, $attr);
                }
                else{
                    throw new IGKInvalidXmlReadException("&lt;! not a valid specification");
                }
            }
            break;default: 
            if($m == 1){
                $n=igk_xml_read_tagname($inf->content, $inf->offset);
                igk_assert_die($n === null, "tagname is null");
                if(($skip_depth > 0) || ($pathlevel > $depth) && ($g && ($g[$depth] != $n))){
                    $skip_depth++;
                }
                if(($skip_depth == 0) && (($pathlevel - 1) == ($depth))){
                    if($callback){
                        if(!$start){
                            if($callback($item)){
                                $start=1;
                            }
                            else
                                $skip_depth=1;
                        }
                        else{
                            if(!$callback($item)){
                                for($i=$depth-1; $i>=0; $i--){
                                    $o .= "</".$g[$i].">";
                                }
                                break 2;
                            }
                        }
                    }
                    $item++;
                }
                $empty=0;
                $attr="";
                igk_xml_read_attribute($inf->content, $inf->offset, $attr);
                $empty=($inf->content[$inf->offset-1] == "/");
                $gap=0;
                if($skip_depth > 0){
                    if($empty){
                        $skip_depth--;
                    }
                    else{
                        $depth++;
                        $gap=0;
                    }
                }
                else{
                    $o .= "<".$n;
                    $o .= $attr;
                    if($empty){
                        $o .= "/>";
                    }
                    else{
                        $o .= ">";
                        $depth++;
                        $gap=0;
                    }
                }
                $m=0;
            }
            else{
                $ggg=strpos($inf->content, "<", $inf->offset);
                if($ggg !== false){
                    $s=trim(substr($inf->content, $inf->offset, $ggg - $inf->offset));
                    if($skip_depth == 0){
                        $o .= $s;
                    }
                    $inf->
                    offset=$ggg-1;
                }
            }
            break;
        }
        $inf->offset++;
    }
    $inf->
    out=$o;
    return $inf->out;
}
///<summary>return a xml representation of the object</summary>
function igk_xml_render($name, $object, $setting=null){
    $tq=array((object)array("n"=>$name, "t"=>$object));
    $out="";
    if(!$setting){
        $setting=(object)["LF"=>IGK_LF, "IndentChar"=>"\t", "FormatText"=>0, "Depth"=>0, "xmlns"=>null, "defaultKeyItem"=>"item_", "contentText"=>"Content"];
    }
    $tq[0]->
    indent=$setting->FormatText ? str_repeat($setting->IndentChar, $setting->Depth): "";
    while($q=array_pop($tq)){
        $out .= $q->indent."<".$q->n;
        $child=0;
        $tab=array();
        foreach($q->t as $k=>$v){
            if($k === $setting->contentText)
                continue;
            if(is_numeric($k)){
                $k=$setting->defaultKeyItem.$k;
            }
            if(is_array($v) || is_object($v)){
                $child=1;
                $c=(object)array(
                        "n"=>$k,
                        "t"=>$v,
                        "p"=>$q,
                        "indent"=>$setting->FormatText ? $q->indent.$setting->IndentChar: ""
                    );
                $q->
                last=$c;
                array_push($tab, $c);
            }
            else
                $out .= " ".$k."=\"".IGKHtmlUtils::GetAttributeValue($v)."\"";
        }
        $lf=$setting->FormatText ? $setting->LF: "";
        $c=!empty($setting->contentText) ? igk_getv($q->t, $setting->contentText): "";
        if(!empty($c)){
            $child=1;
            $c=IGKHtmlUtils::GetValue($c);
        }
        if(!$child)
            $out .= "/>".$lf;
        else{
            if(count($tab) > 0)
                $tq=array_merge($tq, array_reverse($tab));
            $out .= ">".$c.$lf;
            continue;
        }
        while($q && isset($q->p)){
            $l=igk_getv($q->p, "last");
            if($l && ($l === $q)){
                $out .= $q->p->indent."</".$q->p->n.">".$lf;
                $q=$q->p;
            }
            else
                break;
        }
    }
    return $out;
}
///<summary>convert a object stdClass object to core xml node object</summary>
function igk_xml_to_node($obj, $name='objResult', $setting=null){
    $dobj=function(){
        return (object)array("obj"=>null, "node"=>null);
    };
    $tn=igk_is_xmlnode($name) ? $name: igk_createxmlnode($name);
    $cobj=$dobj();
    $cobj->
    node=$tn;
    $cobj->
    obj=$obj;
    $tab=array($cobj);
    $setting=$setting ?? igk_xml_create_to_node_settings();
    $handleArray=$setting->handleArray;
    while(igk_count($tab) > 0){
        $tobj=array_shift($tab);
        $obj=$tobj->obj;
        $n=$tobj->node;
        foreach($obj as $k=>$v){
            $a=is_array($v);
            if($a || is_object($v)){
                if($a && $handleArray($k, $v, $n, $setting))
                    continue;
                $cobj=$dobj();
                $cobj->
                node=$n->addXmlNode($k);
                $cobj->
                obj=$v;
                array_push($tab, $cobj);
            }
            else{
                if($setting->storeAttribAsNode)
                    $n->addNode($k)->
                Content=$v;
                else
                    $n[$k]=$v;
            }
        }
    }
    return $tn;
}
///<summary>convert an igk balafon xml node presentation to stdClass object</summary>
function igk_xml_to_obj($n, $arraycallback=null){
    $obj=igk_createobj();
    $nobj=function(){
        return (object)array("c"=>null, 'obj'=>null);
    };
    if($n && igk_reflection_class_extends($n, IGK_HTML_ITEMBASE_CLASS)){
        $h=$nobj();
        $h->
        c=$n;
        $h->
        obj=$obj;
        $_vlist=array($h);
        $_clist=array();
        while(igk_count($_vlist) > 0){
            $h=array_shift($_vlist);
            $n=$h->c;
            $cobj=$h->obj;
            foreach($n->Attributes as $k=>$v){
                $cobj->
                $k=$v;
            }
            if($n->HasChilds){
                $_clist=array();
                foreach($n->Childs as $k){
                    $v_n=$k->TagName;
                    if(empty($v_n))
                        continue;
                    $j=igk_createobj();
                    if($k->HasChilds){
                        if(isset($cobj->$v_n)){
                            $r=$cobj->$v_n;
                            if(!is_array($r))
                                $r=array($r);
                            $cobj->
                            $v_n=$r;
                        }
                        else
                            $cobj->
                        $v_n=$j;
                        $g=$nobj();
                        $g->
                        c=$k;
                        $g->
                        obj=$j;
                        array_push($_vlist, $g);
                    }
                    else{
                        $g=$nobj();
                        $g->
                        c=$k;
                        $g->
                        obj=$j;
                        array_push($_vlist, $g);
                        if(!isset($cobj->$v_n)){
                            $cobj->
                            $v_n=$j;
                        }
                        else{
                            $vh=$cobj->$v_n;
                            if($arraycallback){
                                $vh=$arraycallback($v_n, $vh, $k->Content);
                            }
                            else{
                                if(!is_array($vh)){
                                    $vh=array($vh);
                                }
                                $vh[]=$j;
                            }
                            $cobj->
                            $v_n=$vh;
                        }
                    }
                }
            }
            else{}
        }
    }
    return $obj;
}
///<summary>Represente igk_xml_type2str function</summary>
///<param name="t"></param>
function igk_xml_type2str($t){
    $tab=array(
            IGKXMLNodeType::NONE=>"NONE",
