}
///<summary>Represente igk_xml_unset_read_info function</summary>
///<param name="inf" ref="true"></param>
function igk_xml_unset_read_info(& $inf){
    if($inf->count === null){
        $inf->
        count=igk_count($inf->objects);
    }
    unset($inf->pathinfo);
    unset($inf->current);
}
///<summary>Represente igk_xml_xpath_objectcallback function</summary>
///<param name="xreader"></param>
///<param name="inf" ref="true"></param>
function igk_xml_xpath_objectcallback($xreader, & $inf){
    if(!isset($inf->cmode)){
        $inf->
        cmode=0;
    }
    $n=$xreader->name;
    switch($xreader->nodetype){
        case IGKXMLNodeType::ENDELEMENT:
        $t=array_shift($inf->pathinfo);
        if($inf->start && ($inf->path == $t)){
            $inf->
            start=0;
            $inf->max--;
            $inf->
            current=null;
            if($inf->max < 0){
                return 0;
            }
        }
        else{
            if($inf->current){
                $inf->
                current=igk_getv($inf->current, 'parent');
            }
        }
        break;
        case IGKXMLNodeType::ELEMENT:
        array_unshift($inf->pathinfo, count($inf->pathinfo) == 0 ? $n: $inf->pathinfo[0]."/{$n}");
        if($inf->start == 0){
            if($inf->pathinfo[0] == $inf->path){
                if($inf->min > $inf->item){
                    igk_xml_read_skip($xreader);
                    $inf->item++;
                    array_shift($inf->pathinfo);
                    return 1;
                }
                $inf->
                start=1;
                $o=igk_createobj($xreader->attribs);
                $inf->
                current=array(
                        "o"=>$o,
                        "parent"=>null,
                        "path"=>$inf->pathinfo[0],
                        "name"=>$n
                    );
                $inf->
                objects[]=$o;
            }
        }
        else{
            $o=null;
            if(igk_count($xreader->attribs) > 0){
                $o=igk_createobj($xreader->attribs);
            }
            if($inf->current['o'] === null){
                $for=$inf->current['name'];
                $co=igk_createobj();
                $co->
                content=$inf->current["parent"]['o']->$for;
                $inf->current["parent"]['o']->
                $for=$co;
                $inf->
                current=array(
                        "o"=>$co,
                        "parent"=>$inf->current["parent"],
                        "name"=>$for
                    );
            }
            $inf->current["o"]->
            $n=$o;
            $inf->
            current=array("o"=>$o, "parent"=>$inf->current);
            if($xreader->isEmpty){
                array_shift($inf->pathinfo);
                $inf->
                current=$inf->current["parent"];
            }
            else{
                $inf->
                current["name"]=$n;
            }
        }
        break;
        case IGKXMLNodeType::TEXT:
        case IGKXMLNodeType::CDATA:
        if($inf->start && $xreader->value && $inf->current){
            $c=$inf->current["name"];
            if(!empty($c)){
                $s=$xreader->value;
                $ii=igk_getv($inf->current['parent']['o'], $c);
                if(!isset($ii)){
                    $inf->current['parent']['o']->
                    $c=$s;
                }
                else{
                    if(is_object($ii)){
                        $ii->
                        content=$s;
                        $inf->current['parent']['o']->$c->
                        content=$s;
                    }
                    else{
                        if(is_string($ii)){
                            $inf->
                            cmode=4;
                            $inf->current['parent']['o']->$c .= $s;
                        }
                        else{
                            $inf->current['parent']['o']->$c->
                            content=$s;
                        }
                    }
                }
            }
        }
        break;
    }
    return 1;
}
///<summary>create xslt transform object</summary>
function igk_xml_xsl_transform($xml, $xslt, & $error=0){
    if($error !== 0){
        libxml_use_internal_errors(true);
    }
    $dom=new DOMDocument();
    $dom->loadXML($xml);
    $xsl=new DOMDocument();
    $xsl->loadXML($xslt);
    $proc=new XSLTProcessor();
    $proc->importStyleSheet($xsl);
    $out=$proc->transformToXML($dom);
    $error=libxml_get_errors();
    return $out;
}
///<summary> create xsl transform node </summary>
function igk_xml_xsl_transformnode($root, $uri=null){
    $p=igk_createpreprocessor("xml-stylesheet");
    $xml=igk_createpreprocessor("xml");
    $xml["version"]="1.0";
    $xml["encoding"]="utf8";
    $h=igk_createxmlnode($root);
    $b=igk_createnode("notagnode");
    $b->add($p);
    $b->add($h);
    $p["href"]=$uri;
    $p["type"]="text/xsl";
    $b->
    root=$h;
    return $b;
}
///<summary>Represente igk_zip_content function</summary>
///<param name="file"></param>
///<param name="name"></param>
///<param name="content"></param>
///<param name="closearchive" default="1"></param>
function igk_zip_content($file, $name, $content, $closearchive=1){
    $zip=new ZipArchive();
    if(!$zip->open($file, ZIPARCHIVE::CREATE)){
        $zip->close();
        return 0;
    }
    $zip->addFromString($name, $content);
    if($closearchive){
        $zip->close();
        return null;
    }
    return $zip;
}
///<summary>Represente igk_zip_create_dir function</summary>
///<param name="outdir"></param>
///<param name="name"></param>
function igk_zip_create_dir($outdir, $name){
    $t=explode('/', $name);
    if(is_dir($outdir)){
        $d=$outdir;
        foreach($t as $k){
            if(empty($k))
                continue;
            $d=$d.DIRECTORY_SEPARATOR.$k;
            if(!is_dir($d))
                @mkdir($d);
        }
    }
}
///<summary>Represente igk_zip_create_file function</summary>
///<param name="file"></param>
///<param name="dir"></param>
///<param name="folder" default="null"></param>
///<param name="regex" default="null"></param>
function igk_zip_create_file($file, $dir, $folder=null, $regex=null){
    if(!is_dir($dir))
        return false;
    $zip=new ZipArchive();
    $zip->open($file, ZIPARCHIVE::CREATE);
    if($zip){
        igk_zip_dir($dir, $zip, $folder, $regex);
        $zip->close();
    }
    return true;
}
///<summary>Represente igk_zip_delete function</summary>
///<param name="file"></param>
///<param name="entry"></param>
///<param name="close" default="1"></param>
function igk_zip_delete($file, $entry, $close=1){
    if(!file_exists($file))
        return 0;
    $zip=new ZipArchive();
    if(!$zip->open($file, ZIPARCHIVE::CREATE)){
        $zip->close();
        return 0;
    }
    $r=$zip->deleteName($entry);
    if($close)
        $zip->close();
    return $r;
}
///<summary>use to zip a directory </summary>
function igk_zip_dir($dir, $zip, $folder=null, $regex=null){
    if(!$zip)
        return;
    $q=0;
    $tab=array($dir);
    while($q=array_pop($tab)){
        $hdir=opendir($q);
        if(is_resource($hdir)){
            while($d=readdir($hdir)){
                if(($d == ".") || ($d == "..") || (($regex !== null) && preg_match($regex, $d))){
                    continue;
                }
                $f=$q."/".$d;
                $hd=substr($f, strlen($dir) + 1);
                $hd=(!empty($folder) ? $folder."/": null).$hd;
                if(is_dir($f)){
                    $zip->addEmptyDir($hd);
                    array_push($tab, $f);
                }
                else if(is_file($f)){
                    $zip->addFile($f, $hd);
                }
            }
            closedir($hdir);
        }
    }
}
///<summary>Represente igk_zip_excludedir function</summary>
///<param name="dir"></param>
///<param name="outf"></param>
///<param name="exclude_pattern"></param>
function igk_zip_excludedir($dir, $outf, $exclude_pattern){
    $files=igk_io_getfiles($dir);
    $zip=new ZipArchive();
    if(file_exists($outf))
        unlink($outf);
    $count=0;
    if($hzi=$zip->open($outf, ZIPARCHIVE::CREATE)){
        $ln=strlen($dir) + 1;
        $tdir=array();
        foreach($files as $k=>$v){
            if(!file_exists($v) || preg_match($exclude_pattern, $v)){
                continue;
            }
            $count++;
            $bf=substr($v, $ln);
            $ddir=dirname($bf);
            if(!isset($tdir[$ddir])){
                $zip->addEmptyDir($ddir);
                $tdir[$ddir]=1;
            }
            if(!is_dir($v))
                $zip->addFile($v, $bf);
        }
        $zip->close();
    }
    return array("count"=>$count, "files"=>$files);
}
