            $tab=array();
            $exp="/^(IGK){0,1}(?P<name>[\w_-]+)(Ctrl|Controller)$/i";
            foreach(get_declared_classes() as $k=>$v){
                if(igk_reflection_class_extends($v, "IGKCtrlTypeBase") && igk_reflection_class_isabstract($v) && preg_match($exp, $v)){
