///<summary>Represente class: IGKFormatString</summary>
final class IGKFormatString implements IIGKHtmlGetValue {
    private $m_args;
    private $m_v;
    ///<summary>Represente __construct function</summary>
    private function __construct(){}
    ///<summary>Represente __toString function</summary>
    public function __toString(){
        return $this->getValue();
    }
    ///<summary>Represente Create function</summary>
    ///<param name="string"></param>
    static public function Create($string){
        $args=array();
        if(func_num_args() > 1){
            $t=func_get_args();
            for($i=1; $i < count($t); $i++){
                $args[]=$t[$i];
            }
        }
        $cfrm=new IGKFormatString();
        $cfrm->
        m_args=$args;
        $cfrm->
        m_v=$string;
        return $cfrm;
    }
    ///<summary>Represente getValue function</summary>
    ///<param name="options" default="null"></param>
    public function getValue($options=null){
        $s=$this->m_v;
        if($this->m_args != null){
            $macth=array();
            $c=preg_match_all("/\{(?P<value>[0-9]+)\}/i", $s, $match);
            for($i=0; $i < $c; $i++){
                $index=$match["value"][$i];
                if(is_numeric($index)){
                    if(isset($this->m_args[$index])){
                        $m=$this->m_args[$index];
                        if(is_string($m)){
                            $s=str_replace($match[0][$i], $m, $s);
                        }
                        else if(is_object($m) && method_exists(get_class($m), IGK_FC_GETVALUE)){
                            $s=str_replace($match[0][$i], $m->getValue(), $s);
                        }
                        else{
                            $s=str_replace($match[0][$i], '', $s);
                        }
                    }
                    else{
                        $s=str_replace($match[0][$i], '', $s);
                    }
                }
            }
        }
        return $s;
    }
}
